import { SET_TYPING_VALUE, SEND_MESSAGE } from "../ActionTypes/ActionTypes";

export default function typing(state = "", action) {
  switch (action.type) {
    case SET_TYPING_VALUE:
      return action.payload;
    case SEND_MESSAGE:
      return "";
    default:
      return state;
  }
}
